// import Vue from "vue"
import { createApp } from 'vue'
import Router from "vue-router"
import Home from "@/views/home/Home.vue" // "../vuews/Home" 이것도 가능

// Vue.use(Router);
createApp.use(Router);

// 특정 페이지 인증이 없이 진입 안돼게도 해보기 => 작성 22/07/02

const routes = [
    // {
    //     path: "/",
    //     redirect: "/sign-in",
    // },
    {
        path: "/",
        name: "Home",
        component: Home // 위에서 컴포넌트 임포트 후 선언 하는 방식
    },
    {
        path: "/communication",
        name: "communication",
        component: () => import('@/views/comm/Communication.vue') // 현재 라인에서 임포트 하자마자 컴포넌트에 주입하는 방식
    },

]

const router = new Router({
    mode: "history",
    routes
});

export default router;
